@startuml
package ATM {
  class Account {
    +accountNumber: int
    +balance: double
  }

  class Customer {
    +customerId: int
    +name: string
    +address: string
  }

  class Transaction {
    +transactionId: int
    +amount: double
    +type: string
  }

  Customer --* Account
  Account --* Transaction

  class ATMSystem {
    + atmList: List<ATM>
  }

  class ATM {
    + atmId: int
    + location: string
  }

  ATMSystem --* ATM

  class Card {
    + cardNumber: int
    + expirationDate: date
    + cvv: int
  }

  class PaymentService <<service>> {
    + processPayment(amount: double): boolean
  }

  class AccountService <<service>> {
    + getAccount(accountNumber: int): Account
    + updateAccount(account: Account): void
  }

  class TransactionService <<service>> {
    + createTransaction(transaction: Transaction): void
  }

  PaymentService --* AccountService
  PaymentService --* TransactionService

  class ATMController <<controller>> {
    + authenticateCard(card: Card): boolean
    + dispenseCash(amount: double): void
  }

  ATMController --* PaymentService

  class SecuritySystem <<external_system>> {
    + authenticateUser(user: Customer): boolean
  }

  ATMController --* SecuritySystem

  class BankDatabase <<external_system>> {
    + getAccount(accountNumber: int): Account
  }

  AccountService --* BankDatabase
}

@enduml

