@startuml
package HospitalManagementSystem {
  class Person {
    +id: int
    +name: string
    +dateOfBirth: date
  }

  class Patient extends Person {
    +medicalHistory: string
    +allergies: string
  }

  class Doctor extends Person {
    +specialty: string
    +licenseNumber: string
  }

  class Nurse extends Person {
    +ward: string
  }

  class MedicalRecord {
    +recordId: int
    +patient: Patient
    +doctor: Doctor
    +diagnosis: string
    +treatment: string
  }

  class Ward {
    +wardId: int
    +wardName: string
    +capacity: int
    +patients: Patient[*]
  }

  class Medication {
    +medicationId: int
    +name: string
    +dosage: string
  }

  class Prescription {
    +prescriptionId: int
    +patient: Patient
    +medication: Medication
    +dosage: string
  }

  class BillingSystem <<external>> {
  }

  class InsuranceProvider <<external>> {
  }

  class Hospital {
    +hospitalId: int
    +name: string
    +wards: Ward[*]
    +patients: Patient[*]
    +doctors: Doctor[*]
    +nurses: Nurse[*]
    +medicalRecords: MedicalRecord[*]
    +billingSystem: BillingSystem
    +insuranceProvider: InsuranceProvider
    {method} admitPatient(patient: Patient)
    {method} dischargePatient(patient: Patient)
  }

  class HospitalService {
    {method} createMedicalRecord(patient: Patient, doctor: Doctor, diagnosis: string, treatment: string)
    {method} prescribeMedication(patient: Patient, medication: Medication, dosage: string)
  }

  Hospital *--o> MedicalRecord
  Hospital *--o> Ward
  Hospital *--o> Patient
  Hospital *--o> Doctor
  Hospital *--o> Nurse
  Patient *--o> Prescription
  Doctor *--o> MedicalRecord
  Ward *--o> Patient
  Medication *--o> Prescription
  HospitalService --* MedicalRecord
  HospitalService --* Prescription
  Hospital --* BillingSystem
  Hospital --* InsuranceProvider
}
@enduml

