@startuml
package clinic {
  class Appointment {
    +id: int
    +date: date
    +time: time
    +patient: Patient
    +doctor: Doctor
  }

  class Patient {
    +id: int
    +name: string
    +email: string
    +phone: string
  }

  class Doctor {
    +id: int
    +name: string
    +specialty: string
  }

  class Clinic {
    +id: int
    +name: string
    +address: string
  }

  class AppointmentService <<service>> {
    +createAppointment(appointment: Appointment): void
    +updateAppointment(appointment: Appointment): void
    +cancelAppointment(appointmentId: int): void
  }

  class AppointmentRepository <<repository>> {
    +getAppointment(appointmentId: int): Appointment
    +getAppointments(patientId: int): List<Appointment>
  }

  class PatientRepository <<repository>> {
    +getPatient(patientId: int): Patient
  }

  class DoctorRepository <<repository>> {
    +getDoctor(doctorId: int): Doctor
  }

  AppointmentService *--o AppointmentRepository
  AppointmentService *--o PatientRepository
  AppointmentService *--o DoctorRepository
  Appointment --* Patient
  Appointment --* Doctor
  Clinic --* Appointment
}

@enduml

