diagram:
@startuml
package LibraryManagementSystem {
  class Book {
    +id: int
    +title: string
    +author: string
    +isbn: string
    +publicationDate: date
  }

  class Member {
    +id: int
    +name: string
    +email: string
    +phoneNumber: string
  }

  class Loan {
    +id: int
    +book: Book
    +member: Member
    +loanDate: date
    +returnDate: date
  }

  class Library {
    +id: int
    +name: string
    +address: string
  }

  class Catalog {
    +id: int
    +library: Library
    +books: Book [*]
  }

  class BorrowingService {
    +loan: Loan
  }

  class LibraryController <<controller>> {
    +library: Library
  }

  class LibraryService <<service>> {
    +library: Library
    +catalog: Catalog
  }

  class BookRepository <<repository>> {
    +books: Book [*]
  }

  class MemberRepository <<repository>> {
    +members: Member [*]
  }

  LibraryController *-- LibraryService
  LibraryService *-- Catalog
  LibraryService *-- BorrowingService
  BorrowingService *-- Loan
  Loan *-- Book
  Loan *-- Member
  Catalog *-- Book
  Library *-- Catalog
  LibraryController *-- BookRepository
  LibraryController *-- MemberRepository
  BookRepository *-- Book
  MemberRepository *-- Member
}
@enduml

